import './App.css';

class App extends Component {
  constructor(props) {
    super(props),
      this.state = {
        setBookName: '',
        setReview: '',
        fetchData: [],
        reviewUpdate: ''
      };
  };

  handleChange = (event) => {
    let nam = event.target.name;
    let val = event.target.value
    this.setState({
      [nam]: val
    });  
  };

  handleChange2 = (event) => {
    this.setState({
      reviewUpdate: event.target.value
    });
  };

  componentDidMount() {
    axios.get("/api/get")
        .then((response) => {
            this.setState({
                fetchData: response.data
            });
        });
  };

  submit = () => {
    axios.post('/api/insert', this.state)
        .then(() => { alert('success post') });
    console.log(this.state);
    document.location.reload();
  };

  remove = (id) => {
    if (confirm("Do you want to delete? ")) {
        axios.delete(`/api/delete/${id}`);
        document.location.reload();
    }
  };

  edit = (id) => {
    axios.put(`/api/update/${id}`, this.state);
    document.location.reload();
  };

  render() {
    let card = this.state.fetchData.map((val, key) => {
        return (
            <React.Fragment>
                <Card style={{ width: '18rem' }} className='m-2'>
                    <Card.Body>
                        <Card.Title>{val.book_name}</Card.Title>
                        <Card.Text>
                            {val.book_review}
                        </Card.Text>
                        <input name='reviewUpdate' onChange={this.handleChange2} placeholder='Update Review' ></input>
                        <Button className='m-2' onClick={() => { this.edit(val.id) }}>Update</Button>
                        <Button onClick={() => { this.delete(val.id) }}>Delete</Button>
                    </Card.Body>
                </Card>
            </React.Fragment>
        );
    });

    return (
        <div className='App'>
            <h1>Dockerized Fullstack React Application</h1>
            <div className='form'>
                <input name='setRepoName' placeholder='Enter Repo Name' onChange={this.handleChange} />
                <input name='setDescription' placeholder='Enter Description' onChange={this.handleChange} />
                <input name='setURL' placeholder='Enter URL' onChange={this.handleChange} />
            </div>
            <Button className='my-2' variant="primary" onClick={this.submit}>Submit</Button> <br /><br />
            <Container>
                <Row>
                    {card}
                </Row>
            </Container>
        </div>
    );
  };

};


export default App;
